import { Component, OnInit } from '@angular/core';
import { ProductService } from '../../services/product.service';
import { Product } from '../../models/product.model';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  products: Product[] = [];
  total: number = 0;

  search = '';
  category = '';
  minPrice = '';
  maxPrice = '';
  page = 1;
  pageSize = 5;

  // Make Math available in the template
  Math = Math;

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.loadProducts();
  }

  loadProducts(): void {
    const filters = {
      search: this.search,
      category: this.category,
      minPrice: this.minPrice,
      maxPrice: this.maxPrice,
      page: this.page,
      pageSize: this.pageSize,
    };

    this.productService.getProducts(filters).subscribe((res) => {
      this.products = res.products;
      this.total = res.total;
    });
  }

  nextPage(): void {
    this.page++;
    this.loadProducts();
  }

  prevPage(): void {
    if (this.page > 1) {
      this.page--;
      this.loadProducts();
    }
  }

  resetFilters(): void {
    this.search = '';
    this.category = '';
    this.minPrice = '';
    this.maxPrice = '';
    this.page = 1;
    this.loadProducts();
  }

  handleImageError(event: any): void {
    // Replace broken image with placeholder
    event.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
  }

  onPageSizeChange(): void {
    // Reset to first page when changing page size
    this.page = 1;
    this.loadProducts();
  }
}

<h1 class="page-title">Our Products</h1>

<div class="filter-panel">
  <div class="filter-group">
    <input [(ngModel)]="search" placeholder="Search by name" class="filter-input" />
    <input [(ngModel)]="category" placeholder="Category" class="filter-input" />
  </div>
  <div class="filter-group">
    <input [(ngModel)]="minPrice" type="number" placeholder="Min Price" class="filter-input" />
    <input [(ngModel)]="maxPrice" type="number" placeholder="Max Price" class="filter-input" />
  </div>
  <div class="filter-actions">
    <button (click)="loadProducts()" class="btn-primary">Search</button>
    <button (click)="resetFilters()" class="btn-secondary">Reset</button>
  </div>
</div>

<div *ngIf="products.length === 0" class="no-products">
  <p>No products found matching your criteria.</p>
</div>

<div class="product-grid">
  <div *ngFor="let p of products" class="product-card">
    <div class="product-image">
      <img [src]="p.image || 'https://via.placeholder.com/300x200?text=Product+Image'" 
           [alt]="p.name" 
           (error)="handleImageError($event)">
    </div>
    <div class="product-info">
      <h3 class="product-name">{{ p.name }}</h3>
      <span class="product-category">{{ p.category }}</span>
      <div class="product-price">₹{{ p.price }}</div>
      <a [routerLink]="['/product', p.id]" class="view-details">View Details</a>
    </div>
  </div>
</div>

<div class="pagination">
  <button (click)="prevPage()" [disabled]="page <= 1" class="btn-secondary">Previous</button>
  <span class="page-info">Page {{ page }} of {{ Math.ceil(total / pageSize) }}</span>
  <button (click)="nextPage()" [disabled]="page * pageSize >= total" class="btn-secondary">Next</button>

  <div class="page-size-selector">
    <label for="pageSize">Items per page:</label>
    <select id="pageSize" [(ngModel)]="pageSize" (change)="onPageSizeChange()">
      <option [value]="5">5</option>
      <option [value]="10">10</option>
      <option [value]="20">20</option>
      <option [value]="50">50</option>
    </select>
  </div>
</div>
